import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { ArrowRightCircle } from "react-bootstrap-icons";
import imgCoding from "../assets/coing.png";
import "animate.css";
import TrackVisibility from "react-on-screen";

export const Banner = () => {
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  const toRotate = ["Web", "Fullstack Javascript", "React"];
  const [text, setText] = useState("");
  const [delta, setDelta] = useState(300 - Math.random() * 100);
  const period = 1000;

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => {
      clearInterval(ticker);
    };
  }, [text]);

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta((prevDelta) => prevDelta / 2);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setDelta(period);
    } else if (isDeleting && updatedText === "") {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setDelta(500);
    }
  };

  return (
    <section className="banner" id="home">
      <Container>
        <Row className="align-items-center">
          <Col xs={12} md={6} xl={7}>
            <TrackVisibility>
              <div></div>
              <span className="tagline">Bienvenue sur mon Portfolio</span>
              <h1>
                {"Développeur "}
                <span className="wrap">{text}</span>
              </h1>
              <p>
                That's my life :
                blablablablablablablablablablablablablablablablablablablablablablablablablabla
              </p>
              <button onClick={() => console.log("connect")}>
                Télécharger mon CV <ArrowRightCircle size={25} />
              </button>
            </TrackVisibility>
          </Col>
          <Col xs={12} md={6} xl={5}>
            <img className="imgBanner" src={imgCoding} alt="Headder Img" />
          </Col>
        </Row>
      </Container>
    </section>
  );
};
