import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { ArrowRightCircle } from "react-bootstrap-icons";
import imgCoding from "../assets/coing.png";
import "animate.css";
import TrackVisibility from "react-on-screen";
import { isVisible } from "@testing-library/user-event/dist/utils";

export const Banner = () => {
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  const toRotate = ["Web", "Fullstack Javascript", "React"];
  const [text, setText] = useState("");
  const [delta, setDelta] = useState(300 - Math.random() * 100);
  const period = 1000;

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => {
      clearInterval(ticker);
    };
  }, [text]);

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta((prevDelta) => prevDelta / 2);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setDelta(period);
    } else if (isDeleting && updatedText === "") {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setDelta(500);
    }
  };

  return (
    <section className="banner" id="home">
      <Container>
        <Row className="align-items-center">
          <Col xs={12} md={6} xl={7}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? "animate__animated animate__bounce" : ""
                  }
                >
                  <span className="tagline">Bienvenue sur mon Portfolio</span>
                  <h1>
                    {"Développeur "}
                    <span className="wrap">{text}</span>
                  </h1>
                  <p>
                    Après 7 années d’expériences dans la réparation de matériel
                    informatique, me voila en reconversion professionnel. Je me
                    suis donc inscris à l'école O’Clock. Grâce à eux, j’ai pu
                    obtenir de nombreuses compétences dans le métier de
                    développeur Fullstack. J’ai pu m’essayer au Front-end et
                    Back-end, j’ai pu tester mes compétences fraîchement
                    acquises sur les parcours de fin de saison. Après quelques
                    mois, il a fallu choisir une spécialisation. Après quelques
                    jours de réflexion, mais surtout avec un goût prononcé pour
                    le Front-end, je me suis dirigé vers la spécialisation
                    React. Après ce mois de spécialisation, ils nous fallait
                    alors accepter l’ultime challenge : “L’Apothéose”. La
                    création d’un projet en équipe qui mettrait nos compétences
                    à rude épreuve mais aussi qui nécessitait l'utilisation de
                    ce que nous avons vu pendant la spécialisation. Ainsi naquit
                    Develott ! Ce fut une belle mise en situation
                    professionnelle qui nous à permis de découvrir notre
                    potentiel et de passer d’apprenant à développeur web junior.
                  </p>
                </div>
              )}
            </TrackVisibility>
          </Col>
          <Col xs={12} md={6} xl={5}>
            <img className="imgBanner" src={imgCoding} alt="Headder Img" />
          </Col>
        </Row>
      </Container>
    </section>
  );
};
